import R from 'ramda'

import markers from './mockMarkers'
import categories from './mockCategories'

export const fetchMarkers = async () => {
  return new Promise(resolve => {
    resolve(markers)
  })
}

export const loadMoreMarkers = async ({offset}) => {
  return new Promise(resolve => {
    resolve(markers)
  })
}

export const fetchMarkerById = async (id) => {
  return new Promise((resolve, reject) => {
    const marker = R.find(R.propEq('id', id), markers)
    resolve(marker)
  })
}

export const fetchCategories = async () => {
    return new Promise(resolve => {
        resolve(categories)
    })
}