import React from 'react'
import {connect} from 'react-redux'
import R from 'ramda'
import {Link} from 'react-router'

import {
    getBasketMarkersWithCount,
    getTotalBasketPrice
} from 'selectors'

import {
    removeMarkerFromBasket,
    basketCheckout,
    cleanBasket
} from 'actions'

const Basket = ({
                    markers,
                    totalPrice,
                    removeMarkerFromBasket,
                    basketCheckout,
                    cleanBasket
}) => {
    const isBasketEmpty = R.isEmpty(markers)

    const renderContent = () => (
        <div>
            {isBasketEmpty && <div>КОРЗИНА ПУСТА</div>}
            <div className='table-responsive'>
                <table className='table-bordered table-striped table-condensed cf'>
                    <tbody>
                        {markers.map((marker, index) => (
                            <tr
                                key={index}
                                className='item-checout'
                            >
                                <td className='first-column-checkout'>
                                    <img
                                        className='img-thumbnail'
                                        src={marker.image}
                                        alt={marker.name}
                                    />
                                </td>
                                <td>{marker.name}</td>
                                <td>{marker.price} грн</td>
                                <td>{marker.count}</td>
                                <td>
                                    <span
                                        onClick={() => removeMarkerFromBasket(marker.id)}
                                        className='delete-cart'
                                    />
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
            {
                R.not(isBasketEmpty) &&
                    <div className='row'>
                        <div className='pull-right total-user-checkout'>
                            <b>ВСЕГО: {totalPrice} грн</b>
                        </div>
                    </div>
            }
        </div>
    )

    const renderSidebar = () => (
        <div>
            <Link
                to='/'
                className='btn btn-info'
            >
                <span className='glyphicon glyphicon-info-sign' />
                <span> Продолжить покупки</span>
            </Link>
            {
                R.not(isBasketEmpty) &&
                <div>
                    <button
                        onClick={cleanBasket}
                        className='btn btn-danger'
                    >
                        <span className='glyphicon glyphicon-trash' />
                         Очистить корзину
                    </button>
                    <button
                        className='btn btn-success'
                        onClick={() => basketCheckout(markers)}
                    >
                        <span className='glyphicon glyphicon-envelope' />
                         Заказать
                    </button>
                </div>
            }
        </div>
    )
    return (
        <div className='view-container'>
            <div className='container'>
                <div className='row'>
                    <div className='col-md-9'>
                        {renderContent()}
                    </div>
                    <div className='col-md-3 btn-user-checkout'>
                        {renderSidebar()}
                    </div>
                </div>
            </div>
        </div>
    )
}

const mapStateToProps = state => ({
    markers: getBasketMarkersWithCount(state),
    totalPrice: getTotalBasketPrice(state)
})

const mapDispatchToProps = {
    removeMarkerFromBasket,
    basketCheckout,
    cleanBasket
}

export default connect(mapStateToProps, mapDispatchToProps)(Basket)