import React, {Component} from 'react'
import {connect} from 'react-redux'
import R from 'ramda'
import {Link} from 'react-router'

import {
    fetchMarkers,
    loadMoreMarkers,
    addMarkerToBasket,
    fetchCategories
} from 'actions'
import {getMarkers} from 'selectors'

class Markers extends Component {
  componentDidMount () {
      this.props.fetchMarkers()
      this.props.fetchCategories()
  }

  renderMarker (marker, index) {
    const {addMarkerToBasket} = this.props
    const shortDescription = `${R.take(30, marker.description)}...`

    return (
      <div className='col-sm-4 col-lg-4 col-md-4 book-list' key={index}>
        <div className='thumbnail'>
          <img
            className='img-thumbnail'
            src={marker.image}
            alt={marker.name}
          />
          <div className='caption'>
            <h4 className='pull-right'>{marker.price} грн</h4>
            <h4>
              <Link to={`/markers/${marker.id}`}>
                {marker.name}
              </Link>
            </h4>
            <p>{shortDescription}</p>
            <p className='itemButton'>
              <button
                className='btn btn-primary'
                onClick={() => addMarkerToBasket(marker.id)}
              >
                Купить!
              </button>
              <Link
                to={`/markers/${marker.id}`}
                className='btn btn-default'
              >
                Подробнее
              </Link>
            </p>
          </div>
        </div>
      </div>
    )
  }


  render () {
    const {markers, loadMoreMarkers} = this.props
    return (
      <div>
        <div className='books row'>
          {markers.map((marker, index) => this.renderMarker(marker, index))}
        </div>
        <div className='row'>
          <div className='col-md-12'>
            <button
              onClick={loadMoreMarkers}
              className='pull-right btn btn-primary'
            >
              Загрузить еще
            </button>
          </div>
        </div>
      </div>

    )
  }
}

const mapStateToProps = (state, ownProps) => {
  return {
    markers: getMarkers(state, ownProps)
  }
}

const mapDispatchToProps = {
    fetchMarkers,
    loadMoreMarkers,
    addMarkerToBasket,
    fetchCategories
}

export default connect(mapStateToProps, mapDispatchToProps)(Markers)
