import R from 'ramda'

import {
    FETCH_MARKERS_SUCCESS,
    LOAD_MORE_MARKERS_SUCCESS,
    SEARCH_MARKER
} from 'actionTypes'

const initialState = {
    ids: [],
    search: ''
}

export default (state = initialState, {type, payload}) => {
  switch (type) {
    case FETCH_MARKERS_SUCCESS:
      return R.merge(state, {
        ids: R.pluck('id', payload)
      })
    case LOAD_MORE_MARKERS_SUCCESS:
      const ids = R.pluck('id', payload)
      return R.merge(state, {
        ids: R.concat(ids, state.ids)
      })
      case SEARCH_MARKER:
        return R.merge(state, {
          search: payload
        })
    default:
      return state
  }
}